'''Ces fonctions ont servi uniquement à la création des maps'''
def destination(map):
    """
    Permet de trouver l'indice des destinations finales de la caisse
    en prenant en argumant la map en cours
    Renvoie une liste de liste composée des coordonnées soit [y,x]
    Correspond à LevelX_destination
    """
    map_desti = []
    for i in range(len(map)):
        for j in range(len(map[i])):
            if map[i][j] == "." or map[i][j] == "*":
                map_desti += [[i,j]]
    #Renvoie sous la forme d'une liste de listes avec [[i,j],[i',k'],...] les indices des destinations
    return map_desti

def affiche(Level,Level_modif):
    """Prend en argument des listes de liste, soit LevelX et LevelX_modif et
    Affiche sous une forme visuelle et compréhensible les listes
    """
    for i in range(len(Level)):
        print(Level[i])
    print('---------------------------')

    for i in range(len(Level_modif)):
        print(Level_modif[i])
    print('---------------------------')

